finalItemTotals.forEach(([name, quantity]) => {
  labels.push(name);
  data.push(quantity);

  let color = '#adb5bd'; // Default grey
  let status = '';
  if (quantity >= 1 && quantity <= 10) {
      color = '#dc3545'; // Red
      status = 'Low';
  } else if (quantity >= 11 && quantity <= 20) {
      color = '#ffc107'; // Yellow
      status = 'Average';
  } else if (quantity >= 21) {
      color = '#198754'; // Green
      status = 'Good';
  }
  backgroundColors.push(color);
  statusLabels.push(status);
});




